package Conditional;

 class nestedfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int week=3;
		int day=7;
		for(int i=1; i<=week; i++)
		{
			System.out.println("weeks="+i);
			for(int j=1; j<=day; j++)
			{
				System.out.println("days="+j);
			}
		}
				
		
	}

}
